definition.script:
#!groovy

// Initialize cfg
cfg = jplConfig('jplBuildIPAHappyTest', 'ios')
cfg.repository.url = 'file:////tmp/jenkins-pipeline-library'
cfg.repository.branch = 'jpl-test'

pipeline {

    agent { label 'master' }

    stages {
        stage ('jplBuildIPA Happy Test - Initialize') {
            steps  {
                echo 'jplBuildIPA Happy Test - Initialize'
                sh "printenv"
                println cfg
                jplStart(cfg)
                sh "echo 'jplBuildIPAHappyTest' > README.md; echo 'jplBuildIPAHappyTest' > random_file.txt"
            }
        }
        stage ('jplBuildIPA Happy Test - Build') {
            steps  {
                echo "jplBuildIPA Happy Test - Build";
                jplBuild(cfg, "echo 'jplBuildIPAHappyTest'")
            }
        }
        stage ('jplBuildIPA Happy Test - Check') {
            steps  {
                echo "jplBuildIPA Happy Test - Check";
                sh "git diff --exit-code"
            }
        }
    }

    options {
        timestamps()
        ansiColor('xterm')
    }
}
