definition.script:
#!groovy

// Initialize cfg
cfg = jplConfig('jplCheckoutSCMTest', 'backend')
cfg.repository.url = 'file:////tmp/jenkins-pipeline-library'
cfg.repository.branch = 'jpl-test'

pipeline {

    agent { label 'master' }

    stages {
        stage ('jplCheckoutSCM Test') {
            steps  {
                echo "jplCheckoutSCM Test";
                sh "printenv"
                println cfg
                jplCheckoutSCM(cfg)
            }
        }
    }

    options {
        timestamps()
        ansiColor('xterm')
    }
}
