definition.script:
#!groovy

// Initialize cfg
cfg = jplConfig('jplDockerPushTest', 'backend')

stage ('jplDockerPush Test') {
  node ("master") {
    echo 'jplDockerPush Test'
    sh "printenv"
    println cfg
    sh 'docker rmi -f jpl-dockerpush-test:test localhost:5000/jpl-dockerpush-test:test 2> /dev/null || true'
    sh 'docker rm -f jpl-registry 2> /dev/null || true'
    sh 'docker run --name jpl-registry -d -p 5000:5000 registry:2'
    deleteDir()
    sh 'mkdir jplDockerPushTempFolder && echo -n "FROM ubuntu\nRUN echo Hello, jplDockerPush" > jplDockerPushTempFolder/Dockerfile'
    sh 'echo "FROM ubuntu" > Dockerfile'
    jplDockerPush(cfg, 'jpl-dockerpush-test', 'test', 'jplDockerPushTempFolder', 'http://localhost:5000')
    sh 'docker rm -f jpl-registry 2> /dev/null || true'
  }
}
