definition.script:
#!groovy

// Initialize cfg
cfg = jplConfig('jplPromoteCodeHappyTest', 'backend')
cfg.BRANCH_NAME="jpl-test"
cfg.repository.url = 'file:////tmp/jenkins-pipeline-library'
cfg.repository.branch = 'jpl-test'

// The pipeline
pipeline {

    agent none

    stages {
        stage ('jplPromoteCode Happy Test - Initialize') {
            agent { label 'master' }
            steps  {
                echo "jplPromoteCode Happy Test - Initialize"
                sh "printenv"
                println cfg
                jplStart(cfg)
            }
        }
        stage ('jplPromoteCode Happy Test - Promote Code') {
            agent { label 'master' }
            steps {
                echo "jplPromoteCode Happy Test - Promote Code";
                sh "echo 'jplPromoteCodeHappyTest' > test/jplPromoteCodeHappyTest.txt; git add test/jplPromoteCodeHappyTest.txt; git commit -m 'Build: Test file for jplPromoteCodeHappyTest';git push"
                jplPromoteCode(cfg, 'jpl-test', 'jpl-test-promoted')
            }
        }
    }

    options {
        timestamps()
        ansiColor('xterm')
    }
}
