definition.script:
#!groovy

// Initialize cfg
cfg = jplConfig('jplDockerBuildTest', 'backend', '', [hipchat: '', slack: '', email: ''])

stage ('jplDockerBuild Test') {
  node {
    echo 'jplDockerBuild Test'
    deleteDir()
    sh 'docker rmi -f jpl-dockerbuild-test:test 2> /dev/null || true'
    sh 'mkdir jplDockerBuildTempFolder && echo -n "FROM ubuntu\nRUN echo Hello, docker" > jplDockerBuildTempFolder/Dockerfile'
    jplDockerBuild(cfg, 'jpl-dockerbuild-test', 'test', 'jplDockerBuildTempFolder')
  }
}
