definition.script:
#!groovy

// Initialize cfg
cfg = jplConfig('jplGitCacheHappyTest', 'backend')
cfg.repository.url = 'file:////tmp/jenkins-pipeline-library'
cfg.repository.branch = 'jpl-test'
cfg.gitCache.enabled = true
def gitCacheProjectAbsolutePath

pipeline {

    agent any

    stages {
        stage ('jplGitCache Happy Test - Start') {
            steps  {
                echo "jplGitCache Happy Test - Start"
                sh "printenv"
                println cfg
                jplStart(cfg)
                script { gitCacheProjectAbsolutePath = "${env.HOME}/${cfg.gitCache.gitCacheProjectRelativePath}" }
                sh "[ -f ${gitCacheProjectAbsolutePath}/.git/config ] && echo 'Cache hitted OK' && touch ${gitCacheProjectAbsolutePath}/.git/cache_flag"
            }
        }
        stage ('jplGitCache Happy Test - Use jpl-git-cache') {
            steps  {
                echo "jplGitCache Happy Test - Use jpl-git-cache"
                deleteDir()
                jplCheckoutSCM(cfg)
                sh "[ -f ${gitCacheProjectAbsolutePath}/.git/cache_flag ] && echo 'Cache retrieved OK'"
            }
        }
    }

    options {
        timestamps()
        ansiColor('xterm')
        skipDefaultCheckout()
    }
}
